#include <iostream>
#include <string>


int main()
{
    std::string number = "";
    
    long final = 0;
    int a;
    
    for (a = 0; a <= 988;a++)
    {
        long temp = 1;
        int b;
        for (b = 0; b <= 12; b++)
        {
            std::string digit = number.substr(a+b,1);                // Substring
            int strToInt = atoi (digit.c_str());                     // Converts string to int
            temp *= strToInt;                                        // Multiply
        }
        
        if (temp > final)                                            // If not greates product
            final = temp;                                            //   make it greatest product
        }
    
    std::cout << final << std::endl;                                 // Display greatest product
    
    
    return 0;
}